sources.styles\\utils\\_reset.scss:
html,
body,
div,
span,
h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
button,
img,
ol,
ul,
li,
form,
input,
textarea,
label,
article,
footer,
header,
nav {
  margin: 0;
  padding: 0;
  border: 0;
  outline: none;
  vertical-align: baseline;
  box-sizing: border-box;
}

a {
  line-height: 0;
  text-decoration: none;
}

