  font-weight: 500;
  color: $c-txt-dark;
}

%flex-row-sb {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: center;
}

%flex-row-c {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
}

