sources.styles\\blocks\\page.scss:
.page {
  font-family: "Mitr", sans-serif;
  font-weight: 400;
  font-size: 20px;

  scroll-behavior: smooth;

  &__body {
    width: 100%;
    height: 100vh;
    background: $c-primary;

    &--menu-is-open {
      overflow: hidden;
    }
  }

  &__header {
    margin: 30px 0 175px;

    @include onTablet {
      margin-bottom: 100px;
    }
  }

  &__menu {
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    z-index: 1;

    transform: translateX(100%);
    transition: transform 0.8s cubic-bezier(0.62, 0, 0.3, 1.25);

    &:target {
      transform: translateX(0);
    }
  }

  &__section {
    padding-bottom: 100px;
  }
}

sources.styles\\utils\\_mixins.scss:
@mixin hover($_property, $_toValue) {
  transition: #{$_property} 0.3s;
  &:hover {
    #{$_property}: $_toValue;
  }
}

@mixin hover-2($_property, $_toValue, $_property-2, $_toValue-2) {
  transition: #{$_property} 0.3s, #{$_property-2} 0.3s;
  &:hover {
    #{$_property}: $_toValue;
    #{$_property-2}: $_toValue-2;
  }
}

@mixin active($_property, $_toValue) {
  transition: #{$_property} 0.1s;
  &:active {
    #{$_property}: $_toValue;
  }
}

@mixin active-2($_property, $_toValue, $_property-2, $_toValue-2) {
  transition: #{$_property} 0.1s, #{$_property-2} 0.1s;
  &:active {
    #{$_property}: $_toValue;
    #{$_property-2}: $_toValue-2;
  }
}

@mixin onTablet {
  @media (max-width: $media-tablet) {
    @content;
  }
}

@mixin onMobile {
  @media (max-width: $media-mobile) {
    @content;
  }
}

sources.styles\\blocks\\header.scss:
.header {
  &__container {
    margin: 0 auto;
  }

  &__wrapper {
    @extend %flex-col;
  }
  
  &__top-action {
    width: 100%;
    @extend %flex-row-sb;
    margin-bottom: 90px;
  }
  
  &__content {
    @extend %flex-row-sb;
    flex-wrap: nowrap;
    width: 100%;

    @include onMobile {
      flex-wrap: wrap;
    }
  }

  &__description {
    @extend %flex-col;
    margin-top: 30px;
    align-items: start;
    flex-basis: 460px;
    
    @include onMobile {
      margin-top: 40px;
      flex-basis: 100%;
      align-items: center;
      order: 2;
    }
  }

  &__title {
    margin-bottom: 40px;

    @include onTablet {
      font-size: 48px;
    }

    @include onMobile {
      font-size: 40px;
      line-height: 150%;
      text-align: center;
    }
  }

  &__text {
    margin-bottom: 90px;

    font-weight: 400;
    font-size: 20px;
    line-height: 150%;
    color: $c-txt-light;

    @include onTablet {
      margin-bottom: 40px;
    }

    @include onMobile {
      min-width: 100%;
      text-align: center;
    }
  }

  &__price {
    font-weight: 400;
    font-size: 16px;
    line-height: 150%;
    color: $c-txt-light-2;

    @include onMobile {
      min-width: 100%;
      text-align: center;
    }
  }

  &__btn {
    width: 300px;
    margin-bottom: 10px;

    @include onMobile {
      max-width: 240px;
      min-width: fit-content;
    }
  }

  &__img-wrapper {
    flex-basis: 510px;
    flex-shrink: 1;
  }

  &__img {
    max-width: 100%;
    object-fit: cover;

    @include hover(transform, scale(1.03));

    @include onMobile {
      width: 100%;
    }
  }
}

sources.styles\\blocks\\top-action.scss:
.top-action {
  &__wrapper {
    @extend %flex-row-sb;
    padding-bottom: 48px;
    width: 100%;
  }

  &__logo {
    width: 210px;

    @include onMobile {
      width: 180px;
    }
  }

  &__right-group {
    @extend %flex-row-sb;

    &--hiden {
      @include onTablet {
        display: none;
      }
    }
  }

  &__nav {
    @include onTablet {
      display: none;
    }
  }

  &__icon {
    display: none;
    width: 32px;
    height: 32px;

    @include onTablet {
      display: block;
    }
  }

  &__btn {
    width: 140px;
    margin-left: 50px;

    @include onMobile {
      display: none;
    }

    &--mobile {
      display: none;

      @include onMobile {
        display: block;
        width: 140px;
      }
    }
  }
}

sources.styles\\blocks\\nav.scss:
.nav {
  &__list {
    @extend %flex-row-sb;

    list-style: none;

    &--tablet {
      flex-direction: column;
    }
  }

  &__item {
    margin-right: 100px;

    font-weight: 400;
    font-size: 20px;
    line-height: 150%;

    &:last-child {
      margin-right: 0;
    }

    &--tablet {
      margin-right: 0;
      margin-bottom: 48px;

      font-size: 48px;
      line-height: 48px;

      &:last-child {
        margin-bottom: 0;
      }
    }
  }

  &__link {
    text-decoration: none;
    color: $c-txt-dark;

    &:hover {
      transform: translateX(5px);
    }

    @include active-2(color, $c-secondary, text-shadow, 0 0 3px $c-secondary);
    @include hover(color, $c-secondary);
  }
}

