sources.styles\\blocks\\about.scss:
.about {
  &__container {
    margin: 0 auto;
  }

  &__section-wrapper {
    margin-bottom: 100px;

    &--subscription {
      padding: 30px 0 20px;
      background-color: $c-bg-about;
    }
  }

  &__section {
    position: relative;
    @extend %flex-row-sb;
    flex-wrap: nowrap;
    align-items: flex-start;

    @include onMobile {
      flex-direction: column;
    }

    &--subscription {
      @extend %flex-col;
      align-items: center;
    }
  }

  &__img-wrapper {
    position: relative;
    flex-basis: 50%;
    overflow: hidden;

    @include onTablet {
      flex-basis: 50%;
    }

    @include onMobile {
      width: 100%;
      align-self: center;
      margin-right: 0;
      padding-bottom: 100%;

      &--order {
        order: -1;
      }
    }
  }

  &__img {
    width: 100%;
    height: 295px;

    object-fit: cover;
    object-position: center;

    @include onMobile {
      position: absolute;
      width: 100%;
      height: 100%;
      padding-bottom: 30px;
    }

    @include hover(transform, scale(1.03));
  }

  &__info {
    flex-basis: 40%;

    @include onTablet {
      flex-basis: 45%;
    }
  }

  &__title {
    padding-bottom: 50px;

    @include onTablet {
      padding-bottom: 30px;
      font-size: 42px;
    }

    &--subscription {
      padding-bottom: 70px;
      text-align: center;
    }
  }

  &__text {
    line-height: 150%;
    color: $c-txt-light;

    @include onTablet {
      font-size: 18px;
    }
  }

  &__subscription-block {
    @extend %flex-row-c;
    margin-bottom: 50px;
  }

  &__subscription-item {
    margin: 0 10px 25px;
    width: 120px;
  }

  &__price {
    font-weight: 400;
    font-size: 16px;
    line-height: 150%;
    color: $c-txt-light-2;

    @include onMobile {
      min-width: 100%;
      text-align: center;
    }
  }

  &__btn {
    width: 300px;
    margin-bottom: 10px;

    @include onMobile {
      max-width: 240px;
      min-width: fit-content;
    }
  }
}

